mapper.select:
select s.st_id ,s.st_gender ,s.st_age ,s.st_phone ,s.st_loc ,s.st_avg
from tpstudent s, tpsugang t where s.st_id = t.sg_student and t.sg_accept = 1
and t.sg_teacher = #{sg_teacher}

mapper.update:
update
    tpteacher
set
	tc_intro = #{tc_intro}
	,tc_phone = #{tc_phone}
	,tc_teachlan = #{tc_teachLan}
	,tc_lan1 = #{tc_lan1}
	,tc_loc = #{tc_loc}
	,tc_price = #{tc_price} 
where
    tc_id =  #{tc_id}

update
    tpstudent
set
	st_age = #{st_age}
	,st_phone = #{st_phone}
	,st_loc = #{st_loc}
	,st_lan1 = #{st_lan1}
	,st_lan2 = #{st_lan2}
	,st_lan3 = #{st_lan3}

where
    st_id =  #{st_id}
